mapper.select:

	select * from (
		select rownum as rnum, a.* from (
			SELECT e.empno, e.ename, e.job, e.mgr, e.hiredate, e.sal, e.comm, d.deptno, d.dname, d.loc FROM newdept d, newemp e 
	WHERE d.deptno = e.deptno AND e.mgr IN (SELECT employee.mgr FROM newemp employee, newemp manager WHERE employee.mgr = manager.empno)
	) a where rownum <= #{pageNum} * #{amount}
	) where rnum > (#{pageNum} -1) * #{amount}
